@push('page-js')
    <script>
        document.addEventListener('livewire:navigated', () => {
                console.log('INIT MAP');

                function makeRegionsHover() {
                    const regions = document.querySelectorAll('.rf-map svg path');
                    const tooltip = document.getElementById('map-tooltip');

                    regions.forEach(region => {
                        region.addEventListener('mouseenter', (e) => {
                            const districtSlug = region.getAttribute('data-district-slug');
                            const regionName = region.getAttribute('data-region') || 'Неизвестный регион';
                            const districtName = region.getAttribute('data-district') || 'Неизвестный округ';

                            // Подсветка всех с тем же data-district-slug
                            regions.forEach(other => {
                                if (other.getAttribute('data-district-slug') === districtSlug) {
                                    other.classList.add('region-hover');
                                }
                            });

                            // Тултип
                            tooltip.innerHTML = `<strong>${districtName}</strong><br>${regionName}`;
                            tooltip.style.display = 'block';
                        });

                        region.addEventListener('mousemove', (e) => {
                            tooltip.style.left = `${e.clientX + 15}px`;
                            tooltip.style.top = `${e.clientY + 15}px`;
                        });

                        region.addEventListener('mouseleave', () => {
                            regions.forEach(el => el.classList.remove('region-hover'));
                            tooltip.style.display = 'none';
                        });
                    });
                }


                function makeRegionsClick() {
                    $('.rf-map svg path, .district-links').on('click', function () {
                        slug = $(this).attr('data-district-slug');
                        if(slug !== 'skfo') {
                            Livewire.dispatch('updateRightCardDistrict', {slug: slug})
                            window.dispatchEvent(new CustomEvent('open-right-card', {
                                detail: 'right-card-district'
                            }));
                        }
                    })
                }

                makeRegionsClick();
                makeRegionsHover();
            }
        );

    </script>
@endpush
